"use client";

import { useState } from "react";
import { useInterval } from "@/hooks/use-interval";

const tabs = [
	{
		title: "Overview",
		description:
			"This tab provides a general overview of your dashboard metrics and recent activity.",
	},
	{
		title: "Analytics",
		description:
			"Detailed analytics and charts are displayed here for deeper insights.",
	},
	{
		title: "Settings",
		description:
			"Manage your preferences and dashboard configuration in this tab.",
	},
];

/**
 * Example component: DashboardCounter
 * Demonstrates useInterval by incrementing a counter every second.
 */
function UseInterval_Ex_02() {
	const [activeTab, setActiveTab] = useState(0);

	useInterval({
		callback: () => setActiveTab((prev) => (prev + 1) % tabs.length),
		delay: 2000, // Switch tab every 2 seconds
	});

	return (
		<div
			className="bg-background border text-center"
			style={{
				padding: 24,
				borderRadius: 8,
				width: 480,
			}}
		>
			<div
				style={{
					display: "flex",
					justifyContent: "center",
					marginBottom: 16,
				}}
			>
				{tabs.map((tab, idx) => (
					<div
						key={tab.title}
						style={{
							flex: 1,
							padding: "8px 0",
							cursor: "pointer",
							borderBottom:
								activeTab === idx
									? "3px solid #0070f3"
									: "1px solid #eaeaea",
							background:
								activeTab === idx ? "#f0f8ff" : "transparent",
							fontWeight: activeTab === idx ? "bold" : "normal",
							transition: "all 0.2s",
						}}
					>
						{tab.title}
					</div>
				))}
			</div>
			<div style={{ minHeight: 60 }}>
				<h3 style={{ margin: "8px 0 4px" }}>
					{tabs[activeTab].title}
				</h3>
				<p style={{ margin: 0 }}>
					{tabs[activeTab].description}
				</p>
			</div>
			<p style={{ marginTop: 16, color: "#888" }}>
				Tabs auto-switch every 2 seconds
			</p>
		</div>
	);
}

export default UseInterval_Ex_02;
